const text = $('AI Agent').first().json.output.response || '';
const maxLength = 3900;
const paragraphs = text.split('\n\n');
const chunks = [];
let currentChunk = '';

paragraphs.forEach(paragraph => {
  if ((currentChunk + paragraph).length > maxLength) {
    if (currentChunk) {
      chunks.push(currentChunk.trim());
      currentChunk = paragraph;
    } else {
      chunks.push(paragraph.substring(0, maxLength) + '...');
    }
  } else {
    currentChunk += (currentChunk ? '\n\n' : '') + paragraph;
  }
});

if (currentChunk) {
  chunks.push(currentChunk.trim());
}

// –í–ê–ñ–ù–û: –¥–æ–±–∞–≤–ª—è–µ–º —Ñ–ª–∞–≥ –¥–ª—è –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ —á–∞–Ω–∫–∞
return chunks.map((chunk, index) => ({
  json: {
    message_text: chunks.length > 1 ? 
      `*–°–æ–æ–±—â–µ–Ω–∏–µ ${index + 1}/${chunks.length}*\n\n${chunk}` : 
      chunk,
    parse_mode: 'MarkdownV2',
    is_last_chunk: index === chunks.length - 1,  // ‚Üê –î–û–ë–ê–í–¨ –≠–¢–û
    total_chunks: chunks.length
  }
}));

