// –û—á–∏—Å—Ç–∫–∞ –¥–ª—è HTML –≤ Telegram
const cleanedText = text
  .replace(/&/g, '&amp;')
  .replace(/</g, '&lt;')
  .replace(/>/g, '&gt;')
  .replace(/\*\*(.*?)\*\*/g, '<b>$1</b>')
  .replace(/\*(.*?)\*/g, '<i>$1</i>')
  .replace(/`(.*?)`/g, '<code>$1</code>')
  .replace(/\[([^\]]+)\]\(([^\)]+)\)/g, '<a href="$2">$1</a>')
  .trim();

return [{
